          "INNER JOIN tipo_parentesco tp ON fj.tipo_parentesco = tp.id WHERE fj.id_jugador = $idJugador";
      if ($result = mysqli_query($con, $sql)) {
        while ($userData = mysqli_fetch_array($result, MYSQLI_ASSOC)) {
          $response->familiares[] = $userData;
        }
      }
      $sql = "SELECT p.*, j.*, (SELECT sexo FROM sexo WHERE id = p.id_sexo) as sexo, ".
      "(SELECT COUNT(*) FROM movimientos WHERE dniJugador = p.dni) as countMovimientos FROM persona p INNER JOIN jugador j ON p.id = j.id ".
      "WHERE p.id = $idJugador";
      if ($result = mysqli_query($con, $sql)) {
        while ($userData = mysqli_fetch_array($result, MYSQLI_ASSOC)) {
          if ($userData['countMovimientos'] > 0){
            $sqlQuotes = "SELECT idTipo, quota, (SELECT IFNULL(SUM(importe),0) FROM movimientos WHERE dniJugador = p.dni AND pagoCompletado = 1 AND tipo_pago = jt.idTipo) as pagado ".
              "FROM jugador_temporada jt INNER JOIN persona p ON jt.idJugador = p.id WHERE idJugador = {$userData['id']}";
            if ($resultQuotes = mysqli_query($con, $sqlQuotes)) {
              $userData['quotes'] = array();
              while ($quotesData = mysqli_fetch_array($resultQuotes, MYSQLI_ASSOC)){
                $quota = array();
                $quota['idTipo'] = (int)$quotesData['idTipo'];
                $quota['quota'] = (float)$quotesData['quota'];
                $quota['pagado'] = (float)$quotesData['pagado'];
                $userData['quotes'][] = $quota;
              }
            }
            $sqlPagos = "SELECT * FROM movimientos WHERE pagoCompletado = 1 AND dniJugador = '{$userData['dni']}'";
            if ($resultPagos = mysqli_query($con, $sqlPagos)) {
              $userData['pagos'] = array();
              while ($pagosData = mysqli_fetch_array($resultPagos, MYSQLI_ASSOC)){
                $pago = array();
                $pago['tipoPago'] = (int)$pagosData['tipo_pago'];
                $pago['descripcion'] = $pagosData['descripcion'];
                $pago['fechaTransaccion'] =  date("d/m/Y", strtotime($pagosData['fechaTransaccion']));
                $pago['importe'] = (float)$pagosData['importe'];
                $userData['pagos'][] = $pago;
              }
            }
          }
          //$response->jugadores[] = $userData;
          $response->jugador = $userData;
        }
      }
    } else {
      $metodoVisualizacion = $params->metodoVisualizacion;
      $sinFamiliaresAsignados = $params->sinFamiliaresAsignados;
      $sinEquiposAsignados = $params->sinEquiposAsignados;
      $conceptoSelecciopnado = $params->conceptoSelecciopnado;
      $estadoJugador = $params->estadoJugador;
      $exclusiones = isset($params->exclusiones) ? $params->exclusiones : null;
      $response->jugadores = array();
      $sql = "SELECT p.*, j.*, (SELECT sexo FROM sexo WHERE id = p.id_sexo) as sexo, ".
          "(SELECT foto FROM fotos WHERE id_persona = p.id ORDER BY id_temporada DESC LIMIT 1) as foto, (SELECT COUNT(*) FROM movimientos WHERE dniJugador = p.dni) as countMovimientos FROM persona p INNER JOIN jugador j ON p.id = j.id ".
          "WHERE p.id IS NOT NULL ";
      if ($sinFamiliaresAsignados == "true") {
        $sql .= "AND (SELECT count(*) FROM familiar_jugador WHERE id_jugador = p.id) = 0 ";
      }
      if ($sinEquiposAsignados == "true") {
        $sql .= "AND (SELECT count(*) FROM equipos_jugadores WHERE id_jugador = p.id AND id_equipo IN (SELECT id FROM equipo WHERE id_temporada = (SELECT id FROM temporada ORDER BY id DESC LIMIT 1))) = 0 ";
      }
      switch ($metodoVisualizacion) {
        case "baja":
          $sql .= "AND j.baja = '1' ";
          break;
        case "alta":
          $sql .= "AND j.baja = '0' ";
          break;
        default:
          break;
      }
      switch ($estadoJugador) {
        case "inscritos":
          $sql .= "AND p.id IN (SELECT idJugador from jugador_temporada where idTipo = $conceptoSelecciopnado) ";
          break;
        case "noInscritos":
          $sql .= "AND p.id NOT IN (SELECT idJugador from jugador_temporada where idTipo = $conceptoSelecciopnado) ";
          break;
        /*case "all":
          $sql .= "AND p.id IN (SELECT idJugador FROM jugador_temporada where idTipo=$conceptoSelecciopnado) ";
          break;*/
        default:
          break;
      }
      if ($exclusiones != null){
        $sql .= "AND p.id NOT IN $exclusiones ";
      }
      $sql .= "ORDER BY p.primer_apellido ASC";
    }
    if ($result = mysqli_query($con, $sql)) {
      while ($userData = mysqli_fetch_array($result, MYSQLI_ASSOC)) {
        if ($userData['countMovimientos'] > 0){
          $sqlQuotes = "SELECT idTipo, quota, (SELECT IFNULL(SUM(importe),0) FROM movimientos WHERE dniJugador = p.dni AND pagoCompletado = 1 AND tipo_pago = jt.idTipo) as pagado ".
            "FROM jugador_temporada jt INNER JOIN persona p ON jt.idJugador = p.id WHERE idJugador = {$userData['id']}";
          if ($resultQuotes = mysqli_query($con, $sqlQuotes)) {
            $userData['quotes'] = array();
            while ($quotesData = mysqli_fetch_array($resultQuotes, MYSQLI_ASSOC)){
              $quota = array();
              $quota['idTipo'] = (int)$quotesData['idTipo'];
              $quota['quota'] = (float)$quotesData['quota'];
              $quota['pagado'] = (float)$quotesData['pagado'];
              $userData['quotes'][] = $quota;
            }
          }
          $sqlPagos = "SELECT * FROM movimientos WHERE pagoCompletado = 1 AND dniJugador = '{$userData['dni']}'";
          if ($resultPagos = mysqli_query($con, $sqlPagos)) {
            $userData['pagos'] = array();
            while ($pagosData = mysqli_fetch_array($resultPagos, MYSQLI_ASSOC)){
              $pago = array();
              $pago['tipoPago'] = (int)$pagosData['tipo_pago'];
              $pago['descripcion'] = $pagosData['descripcion'];
              $pago['fechaTransaccion'] =  date("d/m/Y", strtotime($pagosData['fechaTransaccion']));
              $pago['importe'] = (float)$pagosData['importe'];
              $userData['pagos'][] = $pago;
            }
          }
        }
        $response->jugadores[] = $userData;
      }
    }
    break;
  case 'PUT':
    $params = json_decode(file_get_contents("php://input"));
    if (isset($params->idAssign)) {
      $sql = "INSERT INTO jugador VALUES ($params->idAssign,'0',NULL,'',NULL,NULL)";
    } else {
      $nombre = mysqli_real_escape_string($con, $params->nombre);
      $primer_apellido = mysqli_real_escape_string($con, $params->primer_apellido);
      $segundo_apellido = isset($params->segundo_apellido) ? mysqli_real_escape_string($con, $params->segundo_apellido) : '';
      $dni = mysqli_real_escape_string($con, $params->dni);
      $fecha_nacimiento = $params->fecha_nacimiento;
      $direccion = mysqli_real_escape_string($con, $params->direccion);
      $codigo_postal = mysqli_real_escape_string($con, $params->codigo_postal);
      $localidad = mysqli_real_escape_string($con, $params->localidad);
      $telefono1 = mysqli_real_escape_string($con, $params->telefono1);
      $telefono2 = isset($params->telefono2) ? mysqli_real_escape_string($con, $params->telefono2) : '';
      $email = mysqli_real_escape_string($con, $params->email);
      $observaciones = isset($params->observaciones) ? mysqli_real_escape_string($con, $params->observaciones) : '';
      $id_sexo = $params->id_sexo;
      if (isset($params->baja)) {
        $baja = $params->baja;
        if ($params->baja == '1') {
          $fecha_baja = $params->fecha_baja;
        } else{
          $fecha_baja = NULL;
        }
      } else {
        $baja = '0';
        $fecha_baja = NULL;
      }
      $tarjetaSanitaria = isset($params->tarjetaSanitaria) ? mysqli_real_escape_string($con, $params->tarjetaSanitaria) : '';
      $numero_dorsal = isset($params->numero_dorsal) ? mysqli_real_escape_string($con, $params->numero_dorsal) : '';
      $talla_ropa_juego = isset($params->talla_ropa_juego) ? mysqli_real_escape_string($con, $params->talla_ropa_juego) : '';
      $nombre_dorsal = isset($params->nombre_dorsal) ? mysqli_real_escape_string($con, $params->nombre_dorsal) : '';
      $foto = $params->foto != '' ? mysqli_real_escape_string($con,$params->foto) : "data:image/jpeg;base64,";
      $sql = "UPDATE persona p INNER JOIN jugador j ON p.id = j.id SET nombre = '$nombre', primer_apellido='$primer_apellido', segundo_apellido='$segundo_apellido', dni='$dni', fecha_nacimiento='$fecha_nacimiento', " .
          "direccion='$direccion', codigo_postal='$codigo_postal', localidad='$localidad', telefono1='$telefono1', telefono2='$telefono2', email='$email',observaciones='$observaciones', " .
          "id_sexo = $id_sexo, j.baja = $baja, j.fecha_baja = '$fecha_baja', j.tarjetaSanitaria = '$tarjetaSanitaria', j.numero_dorsal = '$numero_dorsal', j.talla_ropa_juego = '$talla_ropa_juego', j.nombre_dorsal = '$nombre_dorsal' WHERE p.id=$params->id";
    }
    if(mysqli_query($con, $sql))
    {
      if (isset($foto)) {
        if (mysqli_query($con, "INSERT INTO fotos VALUES ($params->id, (SELECT MAX(id) FROM temporada), '$foto') ON DUPLICATE KEY UPDATE foto = '$foto'")){
          return http_response_code(200);
        } else{
          return http_response_code(422);
        }
      } else {
        return http_response_code(200);
      }
    }
    else
    {
      return http_response_code(422);
    }
    break;
  case 'POST':
    $params = json_decode(file_get_contents("php://input"));
    $nombre = mysqli_real_escape_string($con, $params->nombre);
    $primer_apellido = mysqli_real_escape_string($con, $params->primer_apellido);
    $segundo_apellido = isset($params->segundo_apellido) ? mysqli_real_escape_string($con, $params->segundo_apellido) : '';
    $dni = mysqli_real_escape_string($con, $params->dni);
    $fecha_nacimiento = $params->fecha_nacimiento;
    $direccion = mysqli_real_escape_string($con, $params->direccion);
    $codigo_postal = mysqli_real_escape_string($con, $params->codigo_postal);
    $localidad = mysqli_real_escape_string($con, $params->localidad);
    $telefono1 = mysqli_real_escape_string($con, $params->telefono1);
    $telefono2 = isset($params->telefono2) ? mysqli_real_escape_string($con, $params->telefono2) : '';
    $email = mysqli_real_escape_string($con, $params->email);
    $observaciones = isset($params->observaciones) ? mysqli_real_escape_string($con, $params->observaciones) : '';
    $id_sexo = $params->id_sexo;
    $baja = '0';
    $fecha_baja = 'NULL';
    $tarjetaSanitaria = isset($params->tarjetaSanitaria) ? mysqli_real_escape_string($con, $params->tarjetaSanitaria) : '';
    $escuela = $curso = 'NULL';
    $numero_dorsal = isset($params->numero_dorsal) ? mysqli_real_escape_string($con, $params->numero_dorsal) : '';
    $talla_ropa_juego = isset($params->talla_ropa_juego) ? mysqli_real_escape_string($con, $params->talla_ropa_juego) : '';
    $nombre_dorsal = isset($params->nombre_dorsal) ? mysqli_real_escape_string($con, $params->nombre_dorsal) : '';
    $foto = isset($params->foto) ? mysqli_real_escape_string($con,$params->foto) : "data:image/jpeg;base64,";
    $sql = "INSERT INTO persona VALUES (NULL,NULL,'$nombre', '$primer_apellido','$segundo_apellido','$dni','$fecha_nacimiento','$direccion','$codigo_postal','$localidad',".
        "'$telefono1','$telefono2','$email','$observaciones',$id_sexo)";
    if(mysqli_query($con, $sql))
    {
      $idPersona = mysqli_insert_id($con);
      $sql2 = "INSERT INTO jugador VALUES (" . $idPersona . ",0,NULL,'$tarjetaSanitaria',$escuela,$curso)";
      if(mysqli_query($con, $sql2))
      {
        $sql3 = "INSERT INTO fotos VALUES ($idPersona, (SELECT MAX(id) FROM temporada), '$foto') ON DUPLICATE KEY UPDATE foto = '$foto'";
        if(mysqli_query($con, $sql3)) {
          $response->idJugador = $idPersona;
        } else {
          return http_response_code(422);
        }
      }else
      {
        return http_response_code(422);
      }
    }else
    {
      return http_response_code(422);
    }
    break;
  default:
    break;
};
